import { NextResponse } from 'next/server';
import { generatePostAction } from '@/lib/post-generator';

export const maxDuration = 60; // 60 seconds (Vercel Hobby plan limit for serverless)
export const dynamic = 'force-dynamic';

export async function POST(request: Request) {
    try {
        const body = await request.json();
        const { password } = body;

        // Simple security check
        if (password !== '1234') {
            return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
        }

        const result = await generatePostAction('manual');

        if (result.success) {
            return NextResponse.json(result);
        } else {
            return NextResponse.json({ error: result.error }, { status: 500 });
        }

    } catch (error: any) {
        console.error('Manual generation failed:', error);
        return NextResponse.json({ error: error.message || 'Internal Server Error' }, { status: 500 });
    }
}

export async function GET() {
    return NextResponse.json({ status: 'ready', message: 'Manual generation API is ready. Use POST to trigger.' });
}

import { NextResponse } from 'next/server';
import { generatePostAction } from '@/lib/post-generator';

export const dynamic = 'force-dynamic'; // ìºì‹œ ë°©ì§€
export const maxDuration = 55; // Vercel Hobby Timeout (10~60s) ì•ˆì „ë§ˆì§„

export async function GET(request: Request) {
    try {
        // 1. ë³´ì•ˆ ì²´í¬ (CRON_SECRET)
        const authHeader = request.headers.get('authorization');
        const { searchParams } = new URL(request.url);
        const apiKey = searchParams.get('key');

        // Check for essential environment variables
        if (!process.env.CRON_SECRET) {
            console.error('[Cron] CRON_SECRET is missing in environment variables.');
            return NextResponse.json({ error: 'Server Configuration Error: CRON_SECRET missing' }, { status: 500 });
        }

        if (!process.env.SUPABASE_SERVICE_ROLE_KEY) {
            console.error('[Cron] SUPABASE_SERVICE_ROLE_KEY is missing in environment variables.');
            return NextResponse.json({ error: 'Server Configuration Error: SUPABASE_SERVICE_ROLE_KEY missing' }, { status: 500 });
        }

        // í—¤ë” ë˜ëŠ” ì¿¼ë¦¬ íŒŒë¼ë¯¸í„°ë¡œ ì¸ì¦ í—ˆìš© (GitHub Actions/Vercel Cron í˜¸í™˜)
        // [Robustness] Trim whitespace to prevent 401 errors from hidden characters
        const cleanHeader = authHeader?.trim();
        const cleanKey = apiKey?.trim();
        const serverSecret = process.env.CRON_SECRET?.trim();

        const isValid = (cleanHeader === `Bearer ${serverSecret}`) ||
            (cleanKey === serverSecret);

        if (!isValid) {
            console.warn('[Cron] Unauthorized access attempt.', {
                hasAuthHeader: !!authHeader,
                hasApiKey: !!apiKey,
                // Length check for debugging (do not log actual values)
                headerLength: cleanHeader?.length,
                secretLength: serverSecret?.length ? serverSecret.length + 7 : 0
            });
            return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
        }

        // 2. í•œ ë²ˆì— ìƒì„±í•  ê°œìˆ˜ (ê¸°ë³¸ 1ê°œ, ìµœëŒ€ 5ê°œ)
        // Gemini Limit(15 RPM) ë° Vercel Timeout(60s) ê³ ë ¤í•˜ì—¬ 1ë¡œ ì œí•œ
        const limitStr = searchParams.get('limit');
        const limit = Math.min(parseInt(limitStr || '1', 10), 5); // Max 5

        console.log(`[Cron] Starting generation of ${limit} posts...`);

        // 3. ë³‘ë ¬ ì‹¤í–‰ (Promise.all)
        const promises = Array.from({ length: limit }).map(async (_, idx) => {
            // ì´ˆê¸° ì§€ì—° (API ëª°ë¦¼ ë°©ì§€)
            await new Promise(resolve => setTimeout(resolve, idx * 2000));

            // [Retry Logic] ìµœëŒ€ 3ë²ˆ ì¬ì‹œë„ (User requested 5+ -> Updated to 7)
            let result;
            const maxRetries = 7;

            for (let attempt = 1; attempt <= maxRetries; attempt++) {
                try {
                    result = await generatePostAction('auto');

                    if (result.success) {
                        if (attempt > 1) {
                            console.log(`[Cron] Succeeded on attempt ${attempt}`);
                        }
                        return result;
                    }

                    console.warn(`[Cron] Attempt ${attempt}/${maxRetries} failed: ${result.error}`);

                    if (attempt < maxRetries) {
                        const backoff = attempt * 3000; // 3s, 6s...
                        await new Promise(resolve => setTimeout(resolve, backoff));
                    }
                } catch (e: any) {
                    console.error(`[Cron] Critical error on attempt ${attempt}:`, e);
                    if (attempt < maxRetries) await new Promise(resolve => setTimeout(resolve, attempt * 3000));
                }
            }
            return result || { success: false, error: 'Max retries exceeded' };
        });

        const results = await Promise.all(promises);

        const successCount = results.filter(r => r.success).length;
        const failedCount = results.length - successCount;

        console.log(`[Cron] Finished. Success: ${successCount}, Failed: ${failedCount}`);

        return NextResponse.json({
            success: true,
            generated: successCount,
            failed: failedCount,
            details: results,
            version: "v4-abstract-fix-confirmed"
        });

    } catch (error: any) {
        console.error('[Cron] Error:', error);
        return NextResponse.json({ error: error.message }, { status: 500 });
    }
}

